mapper.select:
SELECT * FROM ( SELECT rownum AS rnum, b.* FROM ( SELECT member_id, member_name, member_phone, member_type FROM member_tbl WHERE member_type = 3
ORDER BY enroll_date DESC
ORDER BY enroll_date ASC
) b ) WHERE rnum BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT rownum AS rnum, b.* FROM ( select m.designer_enter, m.member_phone, m.member_name, d.DESIGNER_CAREER , m.member_id from member_tbl m left join designer_tbl d on m.member_id = d.member_id
where m.designer_enter = 0 and m.member_type = 3 and d.designer_career is not null ORDER BY member_name DESC
where m.designer_enter = 2 ORDER BY member_name DESC
where m.designer_enter = 1 ORDER BY member_name DESC
) b ) WHERE rnum BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT rownum AS rnum, b.* FROM (
select member_id, member_name, member_phone, sum(nvl(interior_price,0)) as total_price from member_tbl left join (select * from INTERIOR_TBL where INTERIOR_PAYMENT_DATE between to_char(add_months(sysdate,-3) ,'yyyy-mm-dd') and to_char(sysdate,'yyyy-mm-dd')) on (member_id = interior_designer) where member_type=2 and member_tbl.DESIGNER_ENTER = 1 group by(member_id, member_name, member_phone) order by 4 desc
select member_id, member_name, member_phone, sum(nvl(interior_price,0)) as total_price from member_tbl left join (select * from INTERIOR_TBL where INTERIOR_PAYMENT_DATE between to_char(add_months(sysdate,-6) ,'yyyy-mm-dd') and to_char(sysdate,'yyyy-mm-dd')) on (member_id = interior_designer) where member_type=2 and member_tbl.DESIGNER_ENTER = 1 group by(member_id, member_name, member_phone) order by 4 desc
select member_id, member_name, member_phone, sum(nvl(interior_price,0)) as total_price from member_tbl left join interior_tbl on (member_id = interior_designer) where member_type=2 and member_tbl.DESIGNER_ENTER = 1 group by(member_id, member_name, member_phone) order by 4 desc
) b ) WHERE rnum BETWEEN #{start} AND #{end}

select count(enroll_date) as site_subscriber
from member_tbl
where enroll_date like #{monthDate}
